import jwt from "jsonwebtoken";

export const ensureAuthorized = (config) => {
    console.log('req.path_____',req.path);

  return (req, res, next) => {
    const allowedPaths = [
      "/",
      "/login",
    ];

    if (req.path === "/hasp/uploadCallback") {
      req.headers["bearer"] = req.query.token;
    }

    if (allowedPaths.includes(req.path)) {
      return next();
    }

    const bearerHeader = req.headers["bearer"];
    if (!bearerHeader) {
      return res
        .status(404)
        .json({ message: "No token provided!" });
    }

    const bearerToken = bearerHeader.split(" ")[1];
    jwt.verify(bearerToken, config.jwtKey, (err, decoded) => {
      if (err) {
        return res
          .status(404)
          .json({ message: "Invalid Token!" });
      }
      req.user = decoded;
      next();
    });
  };
};

