import User from "../models/user";
import {
  createUser,
  matchPassword,
  userExists,
} from "../utils/customeFunction";
import { Error, Success } from "../utils/customeResponse";

/**
 * Function is used for user sign-up.
 * @access public
 * @return json
 * Created by Akash
 * @smartData Enterprises (I) Ltd
 * Created Date 19-Nov-2024
 */

const signUp = async (req, res) => {
  try {
    const { firstName, lastName, email, mobile, password, role, bio } =
      req.body;

    // Check if user already exists
    const Exists = await userExists({ mobile: mobile, email: email });
    if (Exists) {
      return Error(res, 409, "User already exists");
    }

    // Add new user to database
    let savedUser = await createUser({
      firstName,
      lastName,
      email,
      mobile,
      password,
      role,
      bio,
    });

    if (savedUser) {
      return Success(res, 200, "User added successfully", savedUser);
    } else {
      return Error(res, 404, "Failed to add user");
    }
  } catch (error) {
    return Error(res, 500, error.message);
  }
};

/**
 * Function is used for user login.
 * @access public
 * @return json
 * Created by Akash
 * @smartData Enterprises (I) Ltd
 * Created Date 18-Nov-2024
 */

const login = async (req, res) => {
  try {
    const { email, password } = req.body;

    // Check if email and password are provided
    if (!email || !password) {
      return Error(res, 400, "Missing email or password");
    }

    // Check if user exists
    let user = await userExists({ email: email });
    if (!user) {
      return Error(res, 404, "User not found");
    }
    // Check password
