sourcesContent:
import User from "../models/user";
import bcrypt from "bcryptjs";
import jwt from "jsonwebtoken";
var salt = bcrypt.genSaltSync(10);
var hash = bcrypt.hashSync("B4c0//", salt);

export const userExists = async (data) => {
  try {
    const user = await User.findOne({
      $or: [{ mobile: data.mobile }, { email: data.email }],
    });
    if (user) {
      return true;
    }
    return false;
  } catch (error) {
    // console.log(error);
    throw error;
  }
};

export const createUser = async (data) => {
  try {
    let addNewUser = new User({
      firstName: data.firstName,
      lastName: data.lastName,
      email: data.email,
      mobile: data.mobile,
      password: bcrypt.hashSync(data.password, 8),
      role: data.role,
    });
    let savedUser = await addNewUser.save();
    if (savedUser) {
      return savedUser;
    }
  } catch (error) {
    // console.log(error);
    throw error;
  }
};

export const matchPassword = async (data) => {
  try {
    let user = await User.findOne({ email: data.email });
    if (user) {
      const isMatch = await bcrypt.compare(data.password, user.password);
      if (isMatch) {
        const { password, ...userWithoutPassword } = user.toObject();

        let token = jwt.sign({ user: userWithoutPassword }, "asdasdasdasd", {
          expiresIn: "1h",
        });
        return token;
      } else {
        return false;
      }
    }
  } catch (error) {
    // console.log(error);
    throw error;
  }
};

